","type":"event"},{"anonymous":false,"inputs":[{"indexed":false,"internalType":"uint256","name":"day","type":"uint256"}],"name":"UpdateUnitsConsumedRan","type":"event"},{"inputs":[{"internalType":"string","name":"_name","type":"string"},{"internalType":"string","name":"_homeAddress","type":"string"}],"name":"addConsumer","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"string","name":"_name","type":"string"},{"internalType":"string","name":"_area","type":"string"},{"internalType":"uint256","name":"_energyAvailableToBuy","type":"uint256"}],"name":"addDistributor","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint256","name":"_energyAvailableToBuy","type":"uint256"}],"name":"addEnergyAvailableToBuy","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"string","name":"_name","type":"string"},{"internalType":"string","name":"_area","type":"string"},{"internalType":"uint256","name":"_energyAvailableToBuy","type":"uint256"}],"name":"addPowerPlant","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"string","name":"_name","type":"string"},{"internalType":"string","name":"_area","type":"string"},{"internalType":"uint256","name":"_energyAvailableToBuy","type":"uint256"}],"name":"addSubstation","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint256","name":"_energyAmount","type":"uint256"}],"name":"buyEnergyFromPowerPlant","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint256","name":"_energyAmount","type":"uint256"}],"name":"buyEnergyFromSubstation","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"calculateEnergyConsumptionOfEachConsumer","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint256","name":"_distributorIndex","type":"uint256"}],"name":"co